mapper.update:
update Original set 

    map_key=#{m_sKey}


    , map_value=#{m_sValue}


    , value_type=#{m_sType}


    , status=#{m_nStatus}


    , key_old=#{m_sKeyOld}

mapper.select:
		select * from Original where 1=1 

            and map_key=#{m_sKey}


            and map_value=#{m_sValue}


            and value_type=#{m_sType}


            and status=#{m_nStatus}


            and key_old=#{m_sKeyOld}
